import { useEffect, useState } from "react";
import axios from "axios";
import styles from "../styles/createquiz.module.css";
import { Link } from "react-router-dom";

const Modal = ({ isOpen, onClose, categories, handleSave }) => {
  const [quizName, setQuizName] = useState("");
  const [category, setCategory] = useState("");
  const user = JSON.parse(localStorage.getItem("user"));
  console.log(user);

  const createQuiz = async () => {
    try {
      const response = await axios.post(
        "http://localhost:8000/api/quiz/test_quiz_create/",
        {
          creator: user.id,
          title: quizName,
          course: user.course,
          category: category,
        },
        {
          headers: {
            "Content-Type": "application/json",
          },
          // withCredentials: true,
        }
      );

      console.log("Quiz created successfully:", response.data);
      localStorage.setItem("createdQuizId", response.data.quiz);
      onClose();

      return response;
    } catch (error) {
      console.error("Error creating quiz:", error);
    }
  };

  if (!isOpen) return null;

  return (
    <div className={styles.modalOverlay}>
      <div className={styles.modalContent}>
        <span className={styles.closeButton} onClick={onClose}>
          X
        </span>{" "}
        <label>Quiz Name </label>
        <input
          type="text"
          placeholder="Untitled Quiz"
          value={quizName}
          onChange={(e) => setQuizName(e.target.value)}
          className={styles.quizNameInput}
        />
        <div className={styles.categories}>
          {categories &&
            categories.map((category) => (
              <button
                key={category.id}
                type="button"
                className={styles.category}
                onClick={(e) => setCategory(category.id)}
              >
                {category.name}
              </button>
            ))}
        </div>
        <button onClick={createQuiz}>Continue</button>
      </div>
    </div>
  );
};

const CreateQuizAlt = () => {
  const [isModalOpen, setIsModalOpen] = useState(true);
  const toggleModal = () => {
    setIsModalOpen(!isModalOpen);
  };

  const [selectedQuestionIndex, setSelectedQuestionIndex] = useState(0);
  const [categories, setCategories] = useState([]);
  let quizInfo = localStorage.getItem("quiz");

  console.log(selectedQuestionIndex);
  const [questions, setQuestions] = useState([
    {
      quiz: 38,
      technique: 1,
      title: "",
      answer: [
        {
          answer_text: "",
          is_correct: false,
        },
        {
          answer_text: "",
          is_correct: false,
        },
        {
          answer_text: "",
          is_correct: false,
        },
        {
          answer_text: "",
          is_correct: false,
        },
      ],
    },
  ]);

  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);
  const handleSave = async () => {
    await saveQuestion(currentQuestionIndex);
  };

  const saveQuestion = async (questionIndex) => {
    try {
      console.log(questionIndex);
      console.log(questions[questionIndex]);
      const response = await axios.post(
        "http://localhost:8000/api/quiz/new_question_create",
        questions[questionIndex],
        {
          headers: {
            "Content-Type": "application/json",
          },
          // withCredentials: true,
        }
      );
      console.log(
        `Question ${questionIndex + 1} saved successfully:`,
        response.data
      );
    } catch (error) {
      console.error(
        `Error saving question ${questionIndex + 1}:`,
        error.message
      );
    }
  };

  useEffect(() => {
    const handleCourseData = async () => {
      try {
        const response = await axios.get(
          "http://localhost:8000/api/quiz/new_quiz_create",
          {
            headers: {
              "Content-Type": "application/json",
            },
            withCredentials: true,
          }
        );
        console.log(response);
        setCategories(response.data.category);
      } catch (error) {
        console.error("Error fetching course data:", error);
      }
    };
    handleCourseData();
  }, []);

  const handleQuestionChange = (index, newText) => {
    const updatedQuestions = [...questions];
    updatedQuestions[index].title = newText;
    setQuestions(updatedQuestions);
  };

  const handleAnswerChange = (answerIndex, newValue) => {
    console.log(answerIndex);
    console.log(newValue);
    console.log(selectedQuestionIndex);
    const updatedQuestions = [...questions];
    updatedQuestions[selectedQuestionIndex].answer[answerIndex].answer_text =
      newValue;
    setQuestions(updatedQuestions);
  };

  const handleRadioChange = (answerIndex) => {
    const updatedQuestions = [...questions];
    updatedQuestions[selectedQuestionIndex].answer[answerIndex].is_correct =
      !updatedQuestions[selectedQuestionIndex].answer[answerIndex].is_correct;
    setQuestions(updatedQuestions);
  };

  const handleAddQuestion = () => {
    console.log(currentQuestionIndex);
    setCurrentQuestionIndex((prevIndex) => prevIndex + 1);
    setQuestions((prevQuestions) => [
      ...prevQuestions,
      {
        quiz: quizInfo ? JSON.parse(quizInfo).id : null,
        technique: 1,
        title: "",
        answer: [
          {
            answer_text: "",
            is_correct: false,
          },
          {
            answer_text: "",
            is_correct: false,
          },
          {
            answer_text: "",
            is_correct: false,
          },
          {
            answer_text: "",
            is_correct: false,
          },
        ],
      },
    ]);
    setSelectedQuestionIndex((prevIndex) => prevIndex + 1);
  };

  return (
    <>
      <nav>
        <Link to="/welcomeuser" className={`${styles.f30} ${styles.f200} `}>
          Quizz
        </Link>
      </nav>

      <div className={styles.createquiz}>
        <aside className={styles.settings}>
          {questions.map((_, index) => (
            <button
              key={index}
              className={styles.qButton}
              onClick={() => setSelectedQuestionIndex(index)}
            >
              {index + 1}
            </button>
          ))}
          <button onClick={handleAddQuestion} className={styles.qButton}>
            +
          </button>
          <button className={styles.btnSaveQuiz} onClick={handleSave}>
            Save Question
          </button>
        </aside>

        <form className={styles.addQuiz}>
          <input
            type="text"
            className={styles.qInput}
            placeholder="question"
            value={questions[selectedQuestionIndex].title}
            onChange={(e) =>
              handleQuestionChange(selectedQuestionIndex, e.target.value)
            }
          />
          <div className={styles.answersInputs}>
            {questions[selectedQuestionIndex].answer.map(
              (answer, answerIndex) => (
                <div key={answerIndex} className={styles.answerFormat}>
                  <input
                    type="checkbox"
                    name="correctAnswer"
                    className={styles.answerRadio}
                    id={`checkbox${answerIndex}`}
                    checked={answer.is_correct}
                    onChange={() => handleRadioChange(answerIndex)}
                  />
                  <input
                    key={answerIndex}
                    className={`${styles[`Answer${answerIndex + 1}`]} ${
                      styles.aInput
                    }`}
                    type="text"
                    name=""
                    id=""
                    placeholder={`Answer ${answerIndex + 1}`}
                    value={answer.answer_text}
                    onChange={(e) =>
                      handleAnswerChange(answerIndex, e.target.value)
                    }
                  />

                  <label htmlFor={`radio${answerIndex}`}></label>
                </div>
              )
            )}
          </div>
        </form>
        <Modal
          isOpen={isModalOpen}
          onClose={toggleModal}
          categories={categories}
        />
      </div>
    </>
  );
};

export default CreateQuizAlt;
