import axios from "axios";
import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import styles from "../styles/getcategoriesadmin.module.css";

function GetCategories() {
  const [categories, setCategories] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const courseResponse = await axios.get("http://localhost:8000/api/quiz/new_quiz_create", {
          headers: {
            "Content-Type": "application/json",
          },
        });

        const categoriesData = courseResponse.data.category.reduce((acc, cat) => {
          acc[cat.id] = cat.name;
          return acc;
        }, {});

        const quizzesResponse = await axios.get("http://localhost:8000/api/quiz/list/", {
          withCredentials: true,
          headers: {
            "Content-Type": "application/json",
            Authorization: `Bearer xqV72-moMK_a_u_QJTHyybjqNfiMlQpZaoyCWPP_St1hs-a3Lw`,
          },
        });

        const quizData = quizzesResponse.data;
        const groupedQuizzes = quizData.reduce((acc, quiz) => {
          const category = quiz.category;

          if (!acc[category]) {
            acc[category] = [];
          }
          const { ...quizInfo } = quiz;
          acc[category].push(quizInfo);

          return acc;
        }, {});

        setCategories({ groupedQuizzes, categoriesData });
      } catch (error) {
        console.error("Error fetching data:", error.message);
      }
    };

    fetchData();
  }, []);

  return (
    <div className={`${styles.dflex} ${styles.dcolumn}`}>
      {Object.entries(categories.groupedQuizzes).map(([categoryId, quizzes]) => (
        <div key={categoryId} className={`${styles.dflex} ${styles.dcolumn}`}>
          <h2 className={styles.quizcategory}>{categories.categoriesData[categoryId]}</h2>
          <div className={`${styles.dgrid}`}>
            {quizzes.map((quiz) => (
              <div key={quiz.id} className={styles.quiz}>
                <div className={styles.quizimage}></div>
                <div className={styles.quizdetails}>
                  <div>
                    <h3 className={styles.quizname}>{quiz.title}</h3>
                  </div>
                  <Link
                    className={styles.linkedit}
                    to={`/quizapp/editquiz/${encodeURIComponent(quiz.title)}`}
                  >
                    Start Quiz
                  </Link>
                </div>
              </div>
            ))}
          </div>
        </div>
      ))}
    </div>
  );
}

export default GetCategories;
