import axios from "axios";
import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import Sidebar from "./Sidebar";

const EditQuizNew = () => {
  const quizId = useParams();
  const quizName = quizId.uuid;
  const [quiz, setQuiz] = useState("");
  const [selectedQuestionIndex, setSelectedQuestionIndex] = useState(null);
//   const [updatedQuestion, setUpdatedQuestion] = {
//     quiz: 0,
//     technique: 1,
//     title: "string",
//     answer: [
//       {
//         id: 0,
//         answer_text: "string",
//         is_correct: true,
//       },
//     ],
//   };

  const editQuiz = async () => {
    const request = await axios.put(
      "http://localhost:8000/api/quiz/new_question_create",
      {
        technique: 1,
        title: quiz[0].title,
        answer: [
          {
            answer_text: "",
            is_correct: false,
          },
          {
            answer_text: "",
            is_correct: false,
          },
          {
            answer_text: "",
            is_correct: false,
          },
          {
            answer_text: "",
            is_correct: false,
          },
        ],
      },
      {
        headers: {
          "Content-Type": "application/json",
        },
      }
    );

    console.log("Quiz created successfully:", response.data);
  };

  useEffect(() => {
    const handleData = async () => {
      try {
        const response = await axios.get(
          `http://localhost:8000/api/quiz/start/${quizName}`,
          {
            headers: {
              "Content-Type": "application/json",
            },
            withCredentials: true,
          }
        );

        const quizData = response.data;
        console.log(quizData);
        setQuiz(quizData);
      } catch (error) {
        console.error("Error fetching quiz:", error.message);
      }
    };

    handleData();
  }, []);

  const handleEditButtonClick = (index) => {
    setSelectedQuestionIndex(index);
  };

  return (
    <div>
      <h1>Edit Quiz Answers</h1>
      {quiz &&
        quiz.map((question, index) => (
          <div key={index}>
            <button onClick={() => handleEditButtonClick(index)}>
              {index}
            </button>
            <p>{question.title}</p>
            {selectedQuestionIndex === index ? (
              question.answer.map((answer, answerIndex) => (
                <input
                  key={answerIndex}
                  type="text"
                  value={answer.answer_text}
                  onChange={(e) => setUpdatedQuestion()}
                />
              ))
            ) : (
              <p>Please choose the question to edit</p>
            )}
          </div>
        ))}
    </div>
  );
};

export default EditQuizNew;
