import { useEffect, useState } from "react";
import axios from "axios";
import styles from "../styles/createquiz.module.css";
import { Link } from "react-router-dom";

const Modal = ({ isOpen, onClose, categories, handleSave }) => {
  const [quizName, setQuizName] = useState("");

  const getCookie = () => {
    const cookies = document.cookie;
    console.log(cookies);
    // for (const cookie of cookies) {
    //   const [cookieName, cookieValue] = cookie.split("=");
    //   if (cookieName === name) {
    //     return decodeURIComponent(cookieValue);
    //   }
    // }
    // return null;
  };

  const sessionId = getCookie("sessionid");
  getCookie("sessionid");
  console.log("Session ID:", sessionId);

  const createQuiz = async () => {
    try {
      const response = await axios.post(
        "http://localhost:8000/api/quiz/test_quiz_create/",
        {
          creator: 31,
          title: "newquiz",
          course: 2,
          category: 2,
        },
        {
          headers: {
            "Content-Type": "application/json",
          },
          withCredentials: true, // Include this to send cookies with the request
        }
      );
      console.log(response);
    } catch (error) {
      console.error("Error creating quiz:", error);
    }
  };

  // createQuiz()
  if (!isOpen) return null;

  return (
    <div className={styles.modalOverlay}>
      <div className={styles.modalContent}>
        <span className={styles.closeButton} onClick={onClose}>
          X
        </span>{" "}
        <label>Quiz Name </label>
        <input
          type="text"
          placeholder="Untitled Quiz"
          value={quizName}
          onChange={(e) => setQuizName(e.target.value)}
          className={styles.quizNameInput}
        />
        <div className={styles.categories}>
          {categories &&
            categories.map((category) => (
              <button
                key={category.id}
                className={styles.category}
                onClick={handleSave}
              >
                {category.name}
              </button>
            ))}
        </div>
        <button onClick={createQuiz}>Continue</button>
      </div>
    </div>
  );
};

const CreateQuizAlt = () => {
  const [questions, setQuestions] = useState([
    {
      quizName: "",
      text: "",
      answers: ["", "", "", ""],
      correctAnswerIndex: null,
    },
  ]);
  const [isModalOpen, setIsModalOpen] = useState(true);

  const toggleModal = () => {
    setIsModalOpen(!isModalOpen);
  };

  const [selectedQuestionIndex, setSelectedQuestionIndex] = useState(0);

  const [categories, setCategories] = useState([]);
  const [quizName, setQuizName] = useState("");

  useEffect(() => {
    const handleCourseData = async () => {
      try {
        const response = await axios.get(
          "http://localhost:8000/api/quiz/new_quiz_create",
          {
            headers: {
              "Content-Type": "application/json",
            },
          }
        );
        console.log(response);
        setCategories(response.data.category);
      } catch (error) {
        console.error("Error fetching course data:", error);
      }
    };
    handleCourseData();
  }, []);

  const handleQuestionChange = (index, newText) => {
    const updatedQuestions = [...questions];
    updatedQuestions[index].text = newText;
    setQuestions(updatedQuestions);
  };

  const handleAnswerChange = (answerIndex, newValue) => {
    const updatedQuestions = [...questions];
    updatedQuestions[selectedQuestionIndex].answers[answerIndex] = newValue;
    setQuestions(updatedQuestions);
    console.log(newValue, answerIndex);
  };

  const handleRadioChange = (answerIndex) => {
    const updatedQuestions = [...questions];
    updatedQuestions[selectedQuestionIndex].correctAnswerIndex = answerIndex;
    setQuestions(updatedQuestions);
  };

  const handleAddQuestion = () => {
    setQuestions((prevQuestions) => [
      ...prevQuestions,
      {
        quizName,
        text: "",
        answers: ["", "", "", ""],
        correctAnswerIndex: null,
        category: "",
      },
    ]);

    setSelectedQuestionIndex((prevIndex) => prevIndex + 1);
  };

  const handleSave = async () => {
    try {
      const quizData = [
        {
          category: categories[0].category,
          quizname: quizName,
          quizquestions: questions.map((question) => ({
            question: question.text,
            answers: question.answers,
            correctAnswerIndex: question.correctAnswerIndex.toString(),
          })),
        },
      ];

      const response = await axios.post(
        "https://crudapi.co.uk/api/v1/quiz",
        quizData,
        {
          headers: {
            "Content-Type": "application/json",
            Authorization:
              "Bearer xqV72-moMK_a_u_QJTHyybjqNfiMlQpZaoyCWPP_St1hs-a3Lw",
          },
        }
      );

      console.log("Quiz saved successfully:", response.data);
    } catch (error) {
      console.error("Error saving quiz:", error.message);
    }
  };

  return (
    <>
      <nav>
        <Link to="/welcomeuser" className={`${styles.f30} ${styles.f200} `}>
          Quizz
        </Link>
      </nav>

      <div className={styles.createquiz}>
        <aside className={styles.settings}>
          <input
            type="text"
            placeholder="Untitled Quiz"
            value={quizName}
            onChange={(e) => setQuizName(e.target.value)}
            className={styles.quizNameInput}
          />
          {questions.map((_, index) => (
            <button
              key={index}
              className={styles.qButton}
              onClick={() => setSelectedQuestionIndex(index)}
            >
              {index + 1}
            </button>
          ))}
          <button onClick={handleAddQuestion} className={styles.qButton}>
            +
          </button>
          <button className={styles.btnSaveQuiz} onClick={toggleModal}>
            Save
          </button>
        </aside>

        <form className={styles.addQuiz}>
          <input
            type="text"
            className={styles.qInput}
            placeholder="question"
            value={questions[selectedQuestionIndex].text}
            onChange={(e) =>
              handleQuestionChange(selectedQuestionIndex, e.target.value)
            }
          />
          <div className={styles.answersInputs}>
            {questions[selectedQuestionIndex].answers.map(
              (answer, answerIndex) => (
                <div key={answerIndex} className={styles.answerFormat}>
                  <input
                    type="radio"
                    name="correctAnswer"
                    className={styles.answerRadio}
                    id={`radio${answerIndex}`}
                    checked={
                      questions[selectedQuestionIndex].correctAnswerIndex ===
                      answerIndex
                    }
                    onChange={() => handleRadioChange(answerIndex)}
                  />
                  <input
                    key={answerIndex}
                    className={`${styles[`Answer${answerIndex + 1}`]} ${
                      styles.aInput
                    }`}
                    type="text"
                    name=""
                    id=""
                    placeholder={`Answer ${answerIndex + 1}`}
                    value={answer}
                    onChange={(e) =>
                      handleAnswerChange(answerIndex, e.target.value)
                    }
                  />

                  <label htmlFor={`radio${answerIndex}`}></label>
                </div>
              )
            )}
          </div>
        </form>
        <Modal
          isOpen={isModalOpen}
          onClose={toggleModal}
          categories={categories}
          handleSave={handleSave}
        />
      </div>
    </>
  );
};

export default CreateQuizAlt;
